"use strict";

/* global define: false */

(function (factory) {

    if ("function" === typeof define) {
        define([
            "data-container/Repository",
            "flow-inspector/Task"
        ], factory);
    } else {
        module.exports = factory.apply(null, [
            require("data-container").Repository,
            require(__dirname + "/Task")
        ]);
    }

}(function (Repository, Task) {

    var TaskRepository;

    /**
     * @auguments data-container/Repository
     * @constructor
     * @param {array} [tasks]
     */
    TaskRepository = function (tasks) {
        Repository.call(this, tasks);
    };
    TaskRepository.prototype = Object.create(Repository.prototype);

    /**
     * @param {JsLoader/FlowInspector/Task} task
     * @return {bool}
     */
    TaskRepository.prototype.isItemAllowed = function (task) {
        return task instanceof Task;
    };

    return TaskRepository;

}));
