"use strict";

/* global define: false */

define(function () {

    var Event; // constructor, function

    /**
     * @constructor
     * @param {mixed} data
     */
    Event = function (data) {
        this.data = data;
        this.isPropagationStopped = false;
    };

    /**
     * @return {void}
     */
    Event.prototype.stopPropagation = function () {
        this.isPropagationStopped = true;
    };

    return Event;

});
