"use strict";

var auto = require("autoloader"),

    // classess
    Event = auto.load("Bender/EventDispatcher/Event");

exports.testThatPropagationIsStopped = function (test) {
    var evt = new Event();

    test.ok(!evt.isPropagationStopped);

    evt.stopPropagation();

    test.ok(evt.isPropagationStopped);

    test.done();
};
