"use strict";

var Configuration = require("configuration-processor").Configuration,
    HeaderConfiguration; // constructor, function

/**
 * @auguments configuration-processor/Configuration
 * @constructor
 * @param {object} options
 */
HeaderConfiguration = function (options) {
    Configuration.call(this, options);
};
HeaderConfiguration.prototype = Object.create(Configuration.prototype);

/**
 * @return {function}
 */
HeaderConfiguration.prototype.expect = function () {
    return this.expectMap(this.expectString());
};

module.exports = HeaderConfiguration;
