"use strict";

var config = require("configuration-processor").configurationProcessor,
    Message, // constructor, function
    Task = require("flow-inspector").Task;

/**
 * @abstract
 * @auguments flow-inspector/Task
 * @constructor
 * @param {Date} date
 */
Message = function (date) {
    Task.call(this);

    this.date = config.assertInstanceOf(Date, date);
};
Message.prototype = Object.create(Task.prototype);

module.exports = Message;
