"use strict";

var mime = require("mime"),
    File, // constructor, function
    MessageConfiguration = require(__dirname + "/../MessageConfiguration");

/**
 * @auguments Bender/HttpServer/MessageConfiguration
 * @constructor
 * @param {string} filename
 */
File = function (filename) {
    MessageConfiguration.call(this, {
        headers: {
            "Content-Type": mime.lookup(filename)
        },
        reasonPhrase: MessageConfiguration.HTTP_OK.REASON_PHRASE,
        statusCode: MessageConfiguration.HTTP_OK.CODE
    });
};
File.prototype = Object.create(MessageConfiguration.prototype);

module.exports = File;
