"use strict";

var InternalServerError, // constructor, function
    MessageConfiguration = require(__dirname + "/../MessageConfiguration");

/**
 * @auguments Bender/HttpServer/MessageConfiguration
 * @constructor
 */
InternalServerError = function () {
    MessageConfiguration.call(this, {
        reasonPhrase: MessageConfiguration.HTTP_INTERNAL_SERVER_ERROR.REASON_PHRASE,
        statusCode: MessageConfiguration.HTTP_INTERNAL_SERVER_ERROR.CODE
    });
};
InternalServerError.prototype = Object.create(MessageConfiguration.prototype);

module.exports = InternalServerError;
