"use strict";

var IncomingMessageQueue = require(__dirname + "/../Incoming"),
    Request, // constructor, function
    RequestIncomingMessage = require(__dirname + "/../Message/Incoming/Request");

/**
 * @auguments Bender/HttpServer/MessageQueue/Incoming
 * @constructor
 */
Request = function () {
    IncomingMessageQueue.call(this);
};
Request.prototype = Object.create(IncomingMessageQueue.prototype);

/**
 * @param {Bender/HttpServer/Message/Incoming/Request} message
 * @return {bool}
 */
Request.prototype.isItemAllowed = function (message) {
    return message instanceof RequestIncomingMessage;
};

module.exports = Request;
