"use strict";

var Outcoming, // constructor, function
    OutcomingMessage = require(__dirname + "/../Message/Outcoming"),
    MessageQueue = require(__dirname + "/../MessageQueue");

/**
 * @auguments Bender/HttpServer/MessageQueue
 * @constructor
 */
Outcoming = function () {
    MessageQueue.call(this);
};
Outcoming.prototype = Object.create(MessageQueue.prototype);

/**
 * @param {Bender/HttpServer/Message/Outcoming} message
 * @return {bool}
 */
MessageQueue.prototype.isItemAllowed = function (message) {
    return message instanceof OutcomingMessage;
};

module.exports = Outcoming;
