"use strict";

var OutcomingMessageQueue = require(__dirname + "/../Outcoming"),
    Response, // constructor, function
    ResponseOutcomingMessage = require(__dirname + "/../../Message/Outcoming/Response");

/**
 * @auguments Bender/HttpServer/MessageQueue/Outcoming
 * @constructor
 */
Response = function () {
    OutcomingMessageQueue.call(this);
};
Response.prototype = Object.create(OutcomingMessageQueue.prototype);

/**
 * @param {Bender/HttpServer/Message/Outcoming/Response} message
 * @return {bool}
 */
Response.prototype.isItemAllowed = function (message) {
    return message instanceof ResponseOutcomingMessage;
};

module.exports = Response;
