"use strict";

var MessageConfiguration = require(__dirname + "/MessageConfiguration"),
    Readable = require("stream").Readable,
    Stream; // constructor, function

/**
 * @auguments stream/Readable
 * @constructor
 */
Stream = function (options) {
    Readable.call(this);

    this.options = new MessageConfiguration(options);
};
Stream.prototype = Object.create(Readable.prototype);

/**
 * @access protected
 * @param {number} size
 * @return {void}
 */
Stream.prototype._read = function (size) {
    this.push(null);
};

module.exports = Stream;
